import React, { useState, useEffect } from "react";
import axios from "axios";
import LayoutApp from "../../components/Layout";
import { Row, Col } from "antd";
import Product from "../../components/Product";
import { useDispatch } from "react-redux";
import "./home.css";

const Home = () => {
  const dispatch = useDispatch();
  const Lhost = "http://localhost:8080";
  const [productData, setProductData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("burgers");
  const categories = [
    {
      name: "pizzas",
      imageUrl: "https://cdn-icons-png.flaticon.com/512/2602/2602303.png",
    },
    {
      name: "burgers",
      imageUrl:
        "data:image/png;base64,",
    },
    {
      name: "drinks",
      imageUrl:
        "https://cdn.iconscout.com/icon/free/png-256/free-beverage-1468789-1242983.png",
    },
  ];

  useEffect(() => {
    const getAllProducts = async () => {
      try {
        dispatch({
          type: "SHOW_LOADING",
        });
        const { data } = await axios.get(`${Lhost}/api/products/get-products`);
        setProductData(data);
        dispatch({
          type: "HIDE_LOADING",
        });
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };

    getAllProducts();
  }, [dispatch]);

  return (
    <LayoutApp>
      <div className="category" style={{ paddingTop: "80px" }}>
        {categories.map((category) => (
          <div
            key={category.name}
            className={`categoryFlex ${
              selectedCategory === category.name && "category-active"
            }`}
            onClick={() => setSelectedCategory(category.name)}
          >
            <h3 className="categoryName">{category.name}</h3>
            <img
              src={category.imageUrl}
              alt={category.name}
              height={100}
              width={100}
            />
          </div>
        ))}
      </div>
      <Row justify="center" style={{ width: "60%", margin: "0 auto" }}>
        {productData
          .filter((i) => i.category === selectedCategory)
          .map((product) => (
            <Col
              xs={24}
              sm={12}
              md={8}
              lg={6}
              className="product-col"
              style={{
                display: "flex",
                justifyContent: "center",
                padding: "10px",
              }}
            >
              <Product key={product.id} product={product} />
            </Col>
          ))}
      </Row>
    </LayoutApp>
  );
};

export default Home;
